import AuthUser from './AuthUser';
import React from 'react'
import { useHistory  } from 'react-router-dom';
const AuthGuard = () => {
    let history = useHistory();
    const {getToken} = AuthUser();
  if(!getToken()){
    return history.push("/login");
  }

    }
export default AuthGuard
